select 
    u.*, 
    oo.name_ as organization_name
  from sys_user u left join sys_organization oo on oo.id=u.organization_id
 where 1=1
  {and name_cn like [name_cn]}
  {and login_id like [login_id_like]}  
  {and organization_id = [organization_id]} 
  {and terminal_agent_id = [terminal_agent_id]}  
  {and type_ = [type_]}  
  {and type_ <> [type_not]}
  {and mobile like [mobile]}  

  {and exists(select 1 from sys_organization o 
               where o.id=u.organization_id
                 and (o.id=[limited_organization_id] or o.pro_organization_id=[limited_organization_id]))}
 order by organization_name, name_cn


select 
    u.*, 
    oo.name_ as organization_name, 
    (select name_cn from sys_user tu where tu.id=u.terminal_agent_id) as terminal_agent_name
  from sys_user u left join sys_organization oo on oo.id=u.organization_id
 where 1=1
  {and name_cn like [name_cn]}
  {and login_id like [login_id_like]}  
  {and organization_id = [organization_id]} 
  {and terminal_agent_id = [terminal_agent_id]}  
  {and type_ = [type_]}  
  {and type_ <> [type_not]}
  {and mobile like [mobile]}  

  {and exists(select 1 from sys_organization o 
               where o.id=u.organization_id
                 and (o.id=[limited_organization_id] or o.pro_organization_id=[limited_organization_id]))}
 order by organization_name, terminal_agent_name, name_cn


select  * from sys_user u where u.status_='A' and exists (
  select 1 from sys_user_role ur where ur.user_id=u.id and exists (
    select 1 from sys_role r 
     where r.id = ur.role_id 
      {and r.name_=[name_]} 
      {and r.code_=[code_]}
      {and r.code_ in ([codes])}
  )
) order by name_cn
